<script lang="ts">
import { defineComponent, computed, onBeforeMount } from 'vue'
import { useRoute } from 'vue-router'
import useAuth from 'src/composables/useAuth'
import { rutaHome } from 'src/router/rutasNombres'
import useMenuRoutes from 'src/composables/useMenuRutes'
import { PERMISO_PERSONAL_ADMIN } from 'src/types/Permisos/constantes'

export default defineComponent({
  name: 'PersonalView',
  setup() {
    const route = useRoute()
    const rutaActual = computed<string>(() => route.name as string)
    const { tienePermiso } = useAuth()
    const { irARutaPorNombre } = useMenuRoutes()

    onBeforeMount(() => {
      if (!tienePermiso(PERMISO_PERSONAL_ADMIN)) {
        irARutaPorNombre(rutaHome)
      }
    })

    return {
      route,
      rutaActual,
      tienePermiso
    }
  }
})
</script>
